sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	const appGr = current.app.getRefRecord();
	if (current.update_set) {
		// only set the app installed_version to this record if it is the latest installed version
		const appVerGr = new GlideRecord('x_376096_mplc_app_version');
		appVerGr.addQuery('app', appGr.sys_id);
		appVerGr.addNotNullQuery('update_set');
		appVerGr.orderByDesc('version');
		appVerGr.setLimit(1);
		appVerGr.query();
		if (appVerGr.next()) {
			if (appVerGr.sys_id == current.sys_id) {
				appGr.installed_version = current.sys_id;
				appGr.update();
			}
		}
	} else {
		if (appGr.getValue('installed_version') == current.sys_id) {
			const appVerGr = new GlideRecord('x_376096_mplc_app_version');
			appVerGr.addQuery('app', appGr.sys_id);
			appVerGr.addNotNullQuery('update_set');
			appVerGr.orderByDesc('version');
			appVerGr.setLimit(1);
			appVerGr.query();
			if (appVerGr.next()) {
				appGr.installed_version = appVerGr.sys_id;
			} else {
				appGr.installed_version = '';
			}

			appGr.update();
		}
	}

})(current, previous);
