sys_ui_action.script:
function mplc_install() {
	if (g_scratchpad.mplc_data.update_sets.length != 1) {
		g_form.addErrorMessage('Import only possible with exactly one Update Set present');
		return;
	}

	var updateSet = g_scratchpad.mplc_data.update_sets[0];
	var gm = new GlideModal('upload');
	gm.setTitle('Update Set Import');
	var uploaded = false;
	gm.renderIframe('upload.do?sysparm_target=sys_remote_update_set', function () {
		if (uploaded) {
			gm.destroy();
			gm = new GlideModalForm(updateSet.update_set_name, 'sys_remote_update_set');
			gm.setSysID(updateSet.update_set_sys_id);
			gm.render();
			return;
		}

		gm.maximizeHeight();

		var iframeDoc = gm.$window.find('iframe').get(0).contentDocument;
		iframeDoc.querySelector('.navbar').style.display = 'none';

		// prevent user from entering data or submitting
		var inputFile = iframeDoc.querySelector('input[type=file]');
		inputFile.setAttribute('readonly', '');

		var submitBtn = iframeDoc.querySelector('input[type=submit]');
		submitBtn.setAttribute('readonly', '');

		var req = new XMLHttpRequest();
		req.responseType = 'blob';
		req.open('get', '/api/now/attachment/' + updateSet.attachment_sys_id + '/file', true);
		req.setRequestHeader('X-UserToken', g_ck);
		req.addEventListener('load', function () {
			var headers = JSON.parse(req.getResponseHeader('X-Attachment-Metadata'));
			var list = new DataTransfer();
			list.items.add(new File([req.response], headers.file_name));

			inputFile.files = list.files;
			submitBtn.click();
			g_form.addInfoMessage('Retrieved Update Set: <a href="/sys_remote_update_set.do?sys_id=' + updateSet.update_set_sys_id + '">LINK</a>')
			uploaded = true;
		});

		req.send();
	});
}

