sys_ui_page.client_script:
/* global document, GlideUI, FormData, URLSearchParams */
/* eslint no-undef: "error" */
(function () {
	var form = document.querySelector('form');
	var submitButton = form.querySelector('button[type=submit]');

	var iframe = form.querySelector('iframe');
	var codeNode = form.querySelector('form input[name=code]');
	codeNode.addEventListener('change', function () {
		if (this.value) submitButton.removeAttribute('disabled');
		else submitButton.setAttribute('disabled', '');
	});

	form.querySelector('button[id=start]').addEventListener('click', function (event) {
		event.preventDefault(); // prevent submit
		this.setAttribute('disabled', '');

		form.querySelector('input[name=state]').setAttribute('readonly', '');
		form.querySelector('.code-instructions').style.display = 'block';
		codeNode.closest('div.row').style.display = 'block';
		submitButton.style.display = 'block';
	});

	form.querySelector('a#oauth-code-ref').addEventListener('click', function (event) {
		this.setAttribute('disabled', '');

		var srcData = new FormData(this.closest('form'));
		var data = new FormData();
		data.set('response_type', 'code');
		['code_challenge', 'client_id', 'client_secret', 'state', 'redirect_uri'].forEach(function (attr) {
			data.set(attr, srcData.get(attr));
		});

		iframe.src = 'https://developer.servicenow.com/oauth_auth.do?' + new URLSearchParams(data);

		codeNode.removeAttribute('readonly');
	});

	// this no longer works because of 'X-Frame-Options' is now 'sameorigin'
	/*oAuthForm.querySelector('a#oauth-token-ref').addEventListener('click', function (event) {
