sysevent_script_action.script:
(function (eventGr, apiKeyGr) {

	/* global event, current, sn_cc, sn_ws, GlideDateTime */
	/* eslint no-undef: "error" */
	if (!apiKeyGr.isValidRecord() || !apiKeyGr.active || !eventGr.parm1) {
		return;
	}

	let context = JSON.parse(eventGr.parm1);
	if (context.update != apiKeyGr.sys_mod_count) {
		return;
	}

	const credentials = new sn_cc.StandardCredentialsProvider().getCredentialByID(apiKeyGr.sys_id);
	const refreshToken = credentials?.getAttribute('authentication_key');
	if (!refreshToken) {
		return;
	}

	const msg = new sn_ws.RESTMessageV2();
	msg.setHttpMethod('post');
	msg.setEndpoint('https://developer.servicenow.com/oauth_token.do');
	msg.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
	msg.setRequestHeader('Accept', 'application/json');
	msg.setRequestBody(Object.entries({
		grant_type: 'refresh_token',
		client_id: 'ff97fbb4da3313004591cc3a291b47fd',
		client_secret: '',
		refresh_token: refreshToken
	}).map(([key, value]) => key + '=' + encodeURIComponent(value)).join('&'));

	const resp = msg.execute();
	if (resp.haveError()) {
		apiKeyGr.active = false;
	} else {
		try {
			const result = JSON.parse(resp.getBody());
			apiKeyGr.api_key = result.access_token;
			apiKeyGr.authentication_key = result.refresh_token;
			apiKeyGr.order = result.expires_in;
		} catch (e) {
			apiKeyGr.active = false;
		}
	}

	apiKeyGr.update();

})(event, current);
